    public static int missingNumber(int n, int arr[]) {
        int sum = 0;
        // sum = (n*(n+1))/2
        for (int i = n; i >= 0; i--) {
            sum += i;
        }
        int total = 0;
        for (int i = 0; i < arr.length; i++) {
            total += arr[i];
        }
        return sum - total;
    }

